mapper.select:
select itemId,productId,name,price,logo,detail
from item
where itemId = #{itemId}

select itemId,productId,name,price,logo,detail
from item
where productId = #{productId}

select count
from
cartItemList
where itemId = #{itemId}
and cartId = #{cartId}

mapper.update:
update cartItemList set
count = #{count}
where cartId = #{cartId}
and itemId = #{itemId}
